% Routine to filter hum from Dirac videos% Anthony Daniell% July 10, 2018%clear allfilename = '/Users/anthonydaniell/Desktop/FilesToStay/Research/AudioProcessing/wav/Dirac_1_of_4.wav';[y, fs] = audioread (filename);% sample signaly_signal = y(1:fs,1);% sample noisey_noise = y(fs+1:2*fs,1);% subtract noise from signalshift_val = -880; % -880 shuft seems to be period%y_clean = y_signal - circshift(y_noise,shift_val);y_clean = y_noise - circshift(y_noise,shift_val);% plot resultssubplot(2,1,1);plot(y_signal(10000:20000), 'b');%subplot(2,1,2);hold on;plot(circshift(y_noise(10000:20000),0), 'r');subplot(2,1,2)plot(y_clean(10500:10500+890));hold off;% create audioplayer and playbackplayer_signal = audioplayer(y_signal, fs, 8);player_noise = audioplayer(y_noise, fs, 8);player_clean = audioplayer(y_clean, fs, 8);play(player_clean)% play(player)%% End of script